import React, {useState} from 'react'
import Post from './Post';

export default function Create() {
  const [title,setTitle] = useState("");
  const [username, setUsername] = useState("");
  const [content, setContent] = useState("");

  function changeHandler(e){
    if (e.target.name === "title"){
      setTitle(e.target.value)
    } else if(e.target.name === "username"){
      setUsername(e.target.value)
    } else if(e.target.name === "content"){
      setContent(e.target.value)
    }
  }

  function submitHandler(){
    <Post username={username} title={title} content={content}/>
  }

  return (
    <div className="Create content">
      <form onSubmit={()=>submitHandler()}>
        <div className="field">
          <div className="title">Username:</div>
          <input type="text" name="username" onChange={(e)=>changeHandler(e)}/>
        </div>
        <div className="field">
          <div className="title">Title:</div>
          <input type="text" name="title" onChange={(e)=>changeHandler(e)}/>
        </div>
        <div className="field">
          <div className="title">Content:</div>
          <textarea name="content" onChange={(e)=>changeHandler(e)}/>
        </div>
        <button type="submit">Create a post</button>
      </form>
    </div>
  )
}
