import React, {useState, useContext} from 'react';

import postContext from '../context';
import Post from './Post';
import Home from './Home';

export default function Create() {
  const {posts, setPosts} = useContext(postContext);

  const [title,setTitle] = useState("");
  const [username, setUsername] = useState("");
  const [content, setContent] = useState("");

  function submitHandler(e){
    e.preventDefault();
    setPosts([...posts, {username: username, title: title, content: content, time: "whenever"}])
    console.log(posts);
    setTitle("");
    setUsername("");
    setContent("");
  }

  return (
    <div className="Create">
      <form onSubmit={submitHandler}>
        <div className="field">
          <div className="title">Username:</div>
          <input type="text" name="username" onChange={(e)=>setUsername(e.target.value)} value={username} placeholder="username..."/>
        </div>
        <div className="field">
          <div className="title">Title:</div>
          <input type="text" name="title" onChange={(e)=>setTitle(e.target.value)} value={title} placeholder="title..."/>
        </div>
        <div className="field">
          <div className="title">Content:</div>
          <textarea name="content" onChange={(e)=>setContent(e.target.value)} value={content} placeholder="content..."/>
        </div>
        <button type="submit">Create a post</button>
      </form>
    </div>
  )
}
