import React, {useState, useContext} from 'react';

import PostContext from '../Context/PostContext';
import ColorContext from '../Context/ColorContext';

import Post from './Post';
import Home from './Home';
import Time from './Time';

export default function Create() {
  const {posts, setPosts} = useContext(PostContext);
  const {color, setColor} = useContext(ColorContext);

  const [title,setTitle] = useState("");
  const [username, setUsername] = useState("");
  const [content, setContent] = useState("");

  // const [r, setR] = useState(100);
  // const [g, setG] = useState(100);
  // const [b, setB] = useState(100);

  function submitHandler(e){
    e.preventDefault();
    setPosts([...posts, {username: username, title: title, content: content, time: ""+Time()}])
    console.log(posts);
    setTitle("");
    setUsername("");
    setContent("");
  }

  return (
    <div className="Create">
      <form onSubmit={submitHandler}>
          <div className="title">Username:</div>
          <input type="text" name="username" onChange={(e)=>setUsername(e.target.value)} value={username} placeholder="✎"/>
          <div className="title">Title:</div>
          <input type="text" name="title" onChange={(e)=>setTitle(e.target.value)} value={title} placeholder="✎"/>
        <div className="field">
          <div className="title">Content:</div>
          <textarea name="content" onChange={(e)=>setContent(e.target.value)} value={content} placeholder="✎"/>
        </div>
        <button type="submit">Create a post</button>
        <div className="color">
          <input style={{backgroundColor: `rgb(${color.r}, ${color.g}, ${color.b}, .8)`}} type="range" min="100" max="255" value={color.r} class="slider" id="red" onChange={(e)=>setColor({...color, "r":e.target.value})}/>
          <input style={{backgroundColor: `rgb(${color.r}, ${color.g}, ${color.b}, .8)`}} type="range" min="100" max="255" value={color.g} class="slider" id="green" onChange={(e)=>setColor({...color, "g":e.target.value})}/>
          <input style={{backgroundColor: `rgb(${color.r}, ${color.g}, ${color.b}, .8)`}} type="range" min="100" max="255" value={color.b} class="slider" id="blue" onChange={(e)=>setColor({...color, "b":e.target.value})}/>
        </div>
      </form>
    </div>
  )
}
