import React, {useContext, useState} from 'react';
import { Link } from 'react-router-dom';
import PostContext from '../Context/PostContext';
import ShowModalContext from '../Context/ShowModalContext';

export default function Post() {
  const {posts, setPosts} = useContext(PostContext);

  // const {edit, setEdit} = useContext(ShowModalContext);
  const {show, setShow} = useContext(ShowModalContext);

  const [remove, setRemove] = useState("");

  function wannaRemove(id){
    if(remove == id){
      setRemove("");
    } else {
      setRemove(id);
    }
  }

  function removePost(id){
    const newPosts = posts.filter((post)=>{
      return post.id != id;
    });
    setPosts(newPosts);
  }

  return(
    posts.map((entry)=>{
      return(
        <div key={entry.id} className="Post-container">
          <div className="Post" style={{backgroundColor: entry.color}}>
            <div onClick={()=>setShow(entry)} className="post-header">
              <small>{entry.time}</small>
              <h4>{entry.title}</h4>
            </div>
            <div className="post-body">
              <p>{entry.content}</p>
            </div>
            <div className="post-footer">
              <div className="icons">
                <div>✏️</div>
                <div onClick={()=>wannaRemove(entry.id)}>❌</div>
                {remove == entry.id ? <p className="sure">remove? <span onClick={()=>removePost(entry.id)}>y</span> / <span onClick={()=>setRemove("")}>n</span></p> : null}
              </div>
              {entry.updated != "" ? <p><small>updated {entry.updated}</small></p> : null}
            </div>
          </div>
        </div>
      )
  })
  )
}
