import React, {useContext} from 'react';
import ShowModalContext from '../Context/ShowModalContext';

export default function ShowModal({entry}) {
  const {show, setShow} = useContext(ShowModalContext);

  console.log(entry);

  return (
    <div className="modal" style={{backgroundColor: `${entry.color}`, backgroundOpacity: ".5"}}>
      <div className="modal-inner">
        <div className="modal-header">
          <small>{entry.time}</small>
          <h4>{entry.title}</h4>
        </div>
        <div className="modal-body">
          <p>{entry.content}</p>
        </div>
        <div className="modal-footer">
          {entry.updated != "" ? <p><small>updated {entry.updated}</small></p> : null}
        </div>
      </div>
    </div>
  )
}
