import React, {useState} from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';

import PostContext from "./Context/PostContext";
import ColorContext from "./Context/ColorContext";

import Navigation from './Components/Navigation';
import Create from './Components/Create';
import ShowAllPosts from './Components/ShowAllPosts';
import Home from './Components/Home';

function App() {
  const [posts, setPosts] =  useState([]);
  const [color, setColor] = useState({"r": 255, "g":255, "b":255});
  const [create, setCreate] = useState(false);
  const [all, setAll] = useState (false);

  return (
    <div className="App" style={{backgroundColor: `rgb(${color.r}, ${color.g}, ${color.b}, .8)`}}>
      <PostContext.Provider value={{posts, setPosts}}>
        <ColorContext.Provider value={{color, setColor}}>
            <Home create={create} setCreate={setCreate}/>
            <h2 onClick={()=>setCreate(!create)}>Make an entry:</h2>
            {create ? <Create/> : null}
            <h2 onClick={()=>setCreate(!all)}>Make an entry:</h2>
            {all ? <ShowAllPosts/> : null}
        </ColorContext.Provider>
      </PostContext.Provider>
    </div>
  );
}

export default App;
