import React, {useContext, useEffect, useState} from 'react';
import ShowModalContext from '../Context/ShowModalContext';
import PostContext from '../Context/PostContext';

export default function ShowModal({entry}) {
  const {show, setShow} = useContext(ShowModalContext);
  const {posts, setPosts} = useContext(PostContext);

  const [remove, setRemove] = useState("");

  function wannaRemove(id){
    if(remove == id){
      setRemove("");
    } else {
      setRemove(id);
    }
  }

  function removePost(id){
    const newPosts = posts.filter((post)=>{
      return post.id != id;
    });
    setShow("");
    setPosts(newPosts);
  }

  useEffect(() => {
    const close = (e) => e.key === 'Escape' ? setShow("") : null;
    window.addEventListener('keydown', close);
    return () => window.removeEventListener('keydown', close);
  },[])

  return (
    <div className="modal">
      <div className="modal-inner-container">
        <div onClick={()=>setShow("")} className="close">×</div>
        <div className="modal-inner" style={{backgroundColor: `${entry.color}`}}>
          <div className="modal-header">
            <small>{entry.time}</small>
            <h4>{entry.title}</h4>
          </div>
          <div className="modal-body">
            <p>{entry.content}</p>
          </div>
          <div className="modal-footer">
            <div className="modal-icons">
              <div>✏️</div>
              <div onClick={()=>wannaRemove(entry.id)}>❌</div>
              {remove == entry.id ? <p className="sure">sure? <span onClick={()=>removePost(entry.id)}>y</span> / <span onClick={()=>setRemove("")}>n</span></p> : null}
            </div>
            {entry.updated != "" ? <p><small>updated {entry.updated}</small></p> : null}
          </div>
        </div>
      </div>
    </div>
  )
}
